""" Basic axis convention class """__all__ = ["Axis"]import numpy as npclass Axis:    def __repr__(self):        return f"Axis(x-hat={self.x_hat}, y-hat={self.y_hat}, z-hat={self.z_hat})"            def __init__(self, test=False):        """        Axis convention for tiles and rotations (i.e., êx x êy = êz)           +y            |            |            |            /--------- +x           /          /        +z        """        self.x_hat = np.array([1, 0, 0])        self.y_hat = np.array([0, 1, 0])        self.z_hat = np.cross(self.x_hat, self.y_hat)        if test is True:            np.testing.assert_almost_equal(np.cross(self.x_hat, self.y_hat), self.z_hat)            np.testing.assert_almost_equal(np.cross(-self.z_hat, -self.y_hat), -self.x_hat)    def rotate(self, mat):        self.x_hat = mat.dot(self.x_hat)        self.y_hat = mat.dot(self.y_hat)        self.z_hat = mat.dot(self.z_hat)