""" Basic 2D face convention class """__all__ = ["Face"]import numpy as npfrom uuid import uuid4class Face:    def __repr__(self):        return f"Face(name={self.name}, center={self.center}, attrs={self.attrs})"    def __init__(self, center: np.ndarray, name: str | int = None, verbose=False):        """            ----------      +1/2            |        |        ^            |  cntr  | -1/2 <   > +1/2            |        |        v            ----------      -1/2        """        center = np.array(center) if isinstance(center, (list, tuple)) else center        self.center = center if isinstance(center, np.ndarray) else np.array([0, 0, 0])        self.name = name        self.key = tuple(self.center)        self.attrs = {"uid": uuid4().hex}    def getAttr(self, attr):        return self.attrs.get(attr)    def setAttr(self, attr, value):        self.attrs[attr] = value