""" Rubik's Cube State Container Class """__all__ = ["RubiksCubeState"]class RubiksCubeState:    def __init__(self, surfaces):        self.state = {cs: {face.getAttr('uid'): face.getAttr('color') for face in faces} for cs, faces in surfaces.items()}    def __eq__(self, rc_state) -> 'bool':        for cs, face_colors in rc_state.state.items():            for face_uid, face_color in face_colors.items():                state_face_color = self.state.get(cs, {}).get(face_uid)                if face_color != state_face_color:                    return False        return True    def __hash__(self):        return hash(tuple([face_color for face_colors in self.state.values() for face_color in face_colors]))